using UnityEngine;using System.Collections;public class PlayerController : GameEntity {	protected void Start(){        base.Start();	}		// Update is called once per frame	protected void Update(){        float axis = Input.GetAxis("Horizontal");        if(axis != 0) {            movement.x = axis;        }        axis = Input.GetAxis("Vertical");        if(axis != 0) {            movement.z = axis;        }                base.Update();	}}